INSERT INTO `wp_comments` (`comment_ID`, `comment_post_ID`, `comment_author`, `comment_author_email`, `comment_author_url`, `comment_author_IP`, `comment_date`, `comment_date_gmt`, `comment_content`, `comment_karma`, `comment_approved`, `comment_agent`, `comment_type`, `comment_parent`, `user_id`) VALUES
(1, 1, 'Một người bình luận WordPress', 'wapuu@wordpress.example', 'https://wordpress.org/', '', '2018-06-17 03:57:23', '2018-06-17 03:57:23', 'Xin chào, đây là một bình luận\nĐể bắt đầu với quản trị bình luận, chỉnh sửa hoặc xóa bình luận, vui lòng truy cập vào khu vực Bình luận trong trang quản trị.\nAvatar của người bình luận sử dụng <a href=\"https://gravatar.com\">Gravatar</a>.', 0, '1', '', '', 0, 0),
(2, 447, 'Brian Gardner', 'demo-comments@studiopress.com', 'http://www.briangardner.com/', '76.29.60.137', '2013-09-01 19:13:54', '2013-09-01 19:13:54', 'This is an example of a comment made on a post. You can either edit the comment, delete the comment or reply to the comment. Use this as a place to respond to the post or to share what you are thinking.', 0, '1', '', '', 0, 1),
(3, 447, 'Brian Gardner', 'demo-comments@studiopress.com', 'http://www.briangardner.com/', '76.29.60.137', '2013-09-01 19:14:04', '2013-09-01 19:14:04', 'This is an example of a nested threaded comment which is new in WordPress 2.7. This is where you can reply to a comment that was previously made, and visually makes reading comments to much easier.', 0, '1', '', '', 2, 1),
(4, 447, 'Brian Gardner', 'demo-comments@studiopress.com', 'http://www.briangardner.com/', '76.29.60.137', '2013-09-01 19:14:11', '2013-09-01 19:14:11', 'This is an example of a comment made on a post. You can either edit the comment, delete the comment or reply to the comment. Use this as a place to respond to the post or to share what you are thinking.', 0, '1', '', '', 0, 1),
(5, 447, 'Brian Gardner', 'demo-comments@studiopress.com', 'http://www.briangardner.com/', '76.29.60.137', '2013-09-01 19:14:19', '2013-09-01 19:14:19', 'This is an example of a nested threaded comment which is new in WordPress 2.7. This is where you can reply to a comment that was previously made, and visually makes reading comments to much easier.', 0, '1', '', '', 4, 1),
(6, 447, 'Brian Gardner', 'demo-comments@studiopress.com', 'http://www.briangardner.com/', '76.29.60.137', '2013-09-01 19:14:25', '2013-09-01 19:14:25', 'This is an example of a nested threaded comment which is new in WordPress 2.7. This is where you can reply to a comment that was previously made, and visually makes reading comments to much easier.', 0, '1', '', '', 5, 1),
(7, 447, 'Brian Gardner', 'demo-comments@studiopress.com', '', '76.29.60.137', '2013-09-01 19:14:38', '2013-09-01 19:14:38', 'This is an example of a comment made on a post. You can either edit the comment, delete the comment or reply to the comment. Use this as a place to respond to the post or to share what you are thinking.', 0, '1', '', '', 0, 1),
(8, 447, 'This is a Sample Trackback', '', 'http://demo.briangardner.com/blog/sample-trackback/', '72.10.51.26', '2013-08-27 00:22:43', '2013-08-27 00:22:43', '[&#8230;] have you seen the sample post with threaded comments over at the Genesis Framework demo [&#8230;]', 0, '1', '', 'pingback', 0, 0),
(9, 447, 'Check Out This Sample Trackback', '', 'http://demo.briangardner.com/blog/sample-trackback-2/', '72.10.51.26', '2013-08-27 00:27:38', '2013-08-27 00:27:38', '[&#8230;] another sample post with threaded comments over at the Genesis Framework demo [&#8230;]', 0, '1', '', 'pingback', 0, 0),
(10, 527, 'admin', 'huutx@vinsofts.net', '', '::1', '2018-06-18 17:52:18', '2018-06-18 10:52:18', 'hi', 0, '1', 'Mozilla/5.0 (Windows NT 6.1; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/67.0.3396.87 Safari/537.36', '', 0, 1);
